import React, { useContext } from "react";
import { BrowserRouter as Router, Route, Routes, Navigate } from "react-router-dom";
import { UserContext, UserProvider } from "./context/UserContext";

import Navbar from "./components/Navbar";
import Navigation from "./components/Navigation";
import HomePage from "./components/HomePage";
import AdminPanel from './components/AdminPanel';
import ProductRegistry from "./components/ProductRegistry";
import Login from "./components/Login";
import Register from "./components/Register";
import ProductsList from "./components/GroupedProductsList";
import PrivateRoute from "./components/PrivateRoute";

import "./App.css";

function App() {
  return (
    <UserProvider> {/* Поміщаємо UserProvider на найвищий рівень */}
      <Router>
        <Navbar />
        <Routes>
          <Route path="/login" element={<LoginRedirect />} />
          <Route path="/" element={<PrivateRoute component={HomePage} />} />
          <Route path="/admin" element={<PrivateRoute component={AdminPanel} />} />
          <Route path="/register" element={<PrivateRoute component={Register} />} />
          <Route path="/products" element={<PrivateRoute component={ProductRegistry} />} />
          <Route path="/productslist" element={<PrivateRoute component={ProductsList} />} />
        </Routes>
        <Navigation />
      </Router>
    </UserProvider>
  );
}

// Окремий компонент для редиректу зі сторінки логіна, якщо користувач вже увійшов
function LoginRedirect() {
  const { user } = useContext(UserContext); // Використовуємо useContext всередині функціонального компоненту

  if (user) {
    return <Navigate to="/" />;
  } else {
    return <Login />;
  }
}

// Оновлений компонент PrivateRoute для забезпечення приватності маршрутів


export default App;
