import { BrowserRouter as Router, Route, Routes, Navigate } from "react-router-dom";

import { UserProvider } from './context/UserContext';

import Navbar from "./components/Navbar";
import Login from "./components/Login";
import Register from "./components/Register";
import HomePage from "./components/HomePage";
import AdminPanel from './components/AdminPanel';
import ProductRegistry from "./components/ProductRegistry";
import ProductsList from "./components/GroupedProductsList";
import PrivateRoute from "./components/PrivateRoute";

import "./App.css";

function App() {
  return (
    <UserProvider>
      <Router>
        <Navbar />
        <Routes>
          <Route path="/login" element={
            <ProtectedRouteWithRedirect redirectTo="/" shouldRedirect={() => !!localStorage.getItem("token")}>
              <Login />
            </ProtectedRouteWithRedirect>
          } />
          <Route path="/" element={<PrivateRoute><HomePage /></PrivateRoute>} />
          <Route path="/admin" element={<PrivateRoute><AdminPanel /></PrivateRoute>} />
          <Route path="/register" element={<PrivateRoute><Register /></PrivateRoute>} />
          <Route path="/products" element={<PrivateRoute><ProductRegistry /></PrivateRoute>} />
          <Route path="/productslist" element={<PrivateRoute><ProductsList /></PrivateRoute>} />
        </Routes>
      </Router>
    </UserProvider>
  );
}

function ProtectedRouteWithRedirect({ children, redirectTo, shouldRedirect }) {
  if (shouldRedirect()) {
    return <Navigate to={redirectTo} />;
  }

  return children;
}

export default App;
